import zlib

# Original text
s = "EE488423D2A208AB74409420F06289F9"
c = ""

# Compress the original text using zlib
compressed = zlib.compress(s.encode('utf-8'))

# Display the compressed result
print(f"Compressed data: {compressed}")
print(f"Compressed size: {len(compressed)} bytes")

# To decompress
decompressed = zlib.decompress(compressed).decode('utf-8')
print(f"Decompressed data: {decompressed}")